void configurarTerminal() {

	HANDLE hOut = GetStdHandle(STD_OUTPUT_HANDLE);
	DWORD dwMode = 0;

	if (GetConsoleMode(hOut, &dwMode)) {
		dwMode |= ENABLE_VIRTUAL_TERMINAL_PROCESSING;
		SetConsoleMode(hOut, dwMode);
	}

	SetConsoleOutputCP(CP_UTF8);
	// DESABILITA ECO E LINE-INPUT no input
    HANDLE hIn = GetStdHandle(STD_INPUT_HANDLE);
    DWORD dwInMode = 0;
    GetConsoleMode(hIn, &dwInMode);
    // retira as flags de ECO e de ler linha inteira
    dwInMode &= ~(ENABLE_ECHO_INPUT | ENABLE_LINE_INPUT);
    SetConsoleMode(hIn, dwInMode);

}
void setConsoleSize(short width, short height) {

	HANDLE hOut = GetStdHandle(STD_OUTPUT_HANDLE);
	COORD bufferSize = { width, height };
	SetConsoleScreenBufferSize(hOut, bufferSize);
	SMALL_RECT windowSize = { 0, 0, (SHORT)(width - 1), (SHORT)(height - 1) };
	SetConsoleWindowInfo(hOut, TRUE, &windowSize);
}

int teclaPressionada(char tecla) {
	SHORT estado = GetAsyncKeyState(tecla);
	return (estado & 0x8000) != 0;
}

