void aguardarSoltarTecla(char tecla) {
	while (teclaPressionada(tecla)) {
		Sleep(10);
	}
}

void exibir_menu_inicial() {
	system("chcp 65001 > nul");    // UTF-8 no Windows
	printf("\033[2J\033[H");       // Limpa tela

	// ----- CORES ANSI -----
	const char *AZUL      = "\033[48;2;0;191;255m";
	const char *FUNDO     = "\033[48;2;30;30;30m";
	const char *BRANCO_TX = "\033[38;2;255;255;255m";
	const char *ROXO_TX   = "\033[38;2;200;120;255m";
	const char *RESET     = "\033[0m";

	// ----- LINHAS DE TEXTO -----
	const char *linhas[] = {
		"CONTROLES:",
		"",
		"A = Esquerda",
		"D = Direita",
		"F = Enter / Pressiona",
		"V = Voltar",
		"",
		"Pronto para comeÃ§ar o jogo?",
		"Digite 1 para prosseguir:"
	};
	const int N = sizeof(linhas) / sizeof(*linhas);

	// ----- DIMENSÃ•ES -----
	int largura_total = 70;           // colunas totais
	int inner = largura_total;    // desconta 1col borda esquerda e 1col borda direita
	int margem = 2;                   // 2 colunas de espaÃ§o entre borda e texto
	int total_linhas = 2 /* top */ + N + 2 /* bottom */;

	// 1) LINHA SUPERIOR
	printf("%s%*s%s\n", AZUL, largura_total, "", RESET);

	// 2) MARGEM SUPERIOR (2 linhas internas vazias)
	for (int i = 0; i < 1; i++) {
		printf("%s%s%*s%s%s\n", AZUL, FUNDO, inner, "", RESET, AZUL);
	}

	// 3) CORPO (texto + linhas vazias para simetria)
	for (int i = 0; i < N; i++) {
		const char *cor_tx = (i >= N-2 ? ROXO_TX : BRANCO_TX);
		int len = (int)strlen(linhas[i]);
		// borda esquerda
		printf("%s", AZUL);
		// fundo e margem esquerda
		printf("%s%*s", FUNDO, margem, "");
		// texto
		printf("%s%s", cor_tx, linhas[i]);
		// preenche resto do fundo atÃ© chegar na margem direita
		int resto = inner - margem - len - margem;
		printf("%s%*s", FUNDO, resto, "");
		// margem direita + borda direita
		printf("%s%*s%s%s\n", FUNDO, margem, "", AZUL, RESET);
	}

	// 4) MARGEM INFERIOR (1 linha interna vazia)
	for (int i = 0; i < 1; i++) {
		printf("%s%s%*s%s%s\n", AZUL, FUNDO, inner, "", AZUL, RESET);
	}

	// 5) LINHA INFERIOR
	printf("%s%*s%s\n", AZUL, largura_total, "", RESET);

	// 6) posiciona cursor logo apÃ³s "Digite 1 para prosseguir:"
	int linha_cursor  = 1 /* top */ + 1 /* margem sup */ + N /* linhas de texto */ + 0;
	int coluna_cursor = 2 /* borda */ + margem + (int)strlen("Digite 1 para prosseguir:");
	printf("\033[%d;%dH", linha_cursor, coluna_cursor);
}

